import * as dat from "dat.gui";
import gsap from "gsap"; //动画库
export function createGui(cube) {
  // 创建gui
  const gui = new dat.GUI();
  gui
    .add(cube.position, "x")
    .min(0)
    .max(5)
    .step(0.1)
    .name("cube X轴")
    .onChange((value) => {})
    .onFinishChange((value) => {});
  let params = {
    color: "#ff0000",
    fn() {
      gsap.to(cube.position, {
        x: 5,
        duration: 3,
        // 重复次数 -1为无限次
        repeat: -1,
        // 往返
        yoyo: true,
        // 延迟
        ease: "powerl.inOut",
      });
    },
  };
  gui.addColor(params, "color").onChange((value) => {
    cube.material.color.set(value);
  });
  gui.add(cube, "visible");
  gui.add(params, "fn").name("运动");
  // 创建一个文件夹
  let folder = gui.addFolder("设置立方体");
  folder.add(cube.material, "wireframe");
}

//
export function createAnimate(cube) {
  // 设置动画
  let animate = gsap.to(cube.position, {
    x: 5,
    duration: 3,
    // 重复次数 -1为无限次
    repeat: -1,
    // 往返
    yoyo: true,
    // 延迟
    delay: 1,
    ease: "powerl.inOut",
    onComplete() {
      console.log(123);
    },
  });
  gsap.to(cube.rotation, {
    x: Math.PI * 2,
    duration: 5,
    repeat: -1,
    yoyo: true,
    ease: "powerl.inOut",
  });
  return animate;
}
